/**
 * Parse the <code>EXT_structural_metadata</code> glTF extension to create a
 * structural metadata object.
 *
 * @param {object} options Object with the following properties:
 * @param {object} options.extension The extension JSON object.
 * @param {MetadataSchema} options.schema The parsed schema.
 * @param {Object<string, Uint8Array>} [options.bufferViews] An object mapping bufferView IDs to Uint8Array objects.
 * @param {Object<string, Texture>} [options.textures] An object mapping texture IDs to {@link Texture} objects.
 * @return {StructuralMetadata} A structural metadata object
 * @private
 * @experimental This feature is using part of the 3D Tiles spec that is not final
 * and is subject to change without Cesium's standard deprecation policy.
 */
export function parseStructuralMetadata(options: {
    extension: object;
    schema: MetadataSchema;
    bufferViews?: {
        [x: string]: Uint8Array<ArrayBuffer>;
    } | undefined;
    textures?: {
        [x: string]: Texture;
    } | undefined;
}): StructuralMetadata;
export function createChildStructuralMetadata(rootStructuralMetadata: any, extensions: any): StructuralMetadata;
import StructuralMetadata from './StructuralMetadata.js';
