/**
 * 窗户特效动画材质
 * ```
 * var material = new mapvthree.WindowMaterial({
 *    color: new THREE.Color(0xffffff),
 *    size: [1, 1],
 *    effectSize: [1, 1],
 *    speed: 1,
 *    emissive: new THREE.Color(0x001155),
 *    direction: mapvthree.WindowMaterial.DIRECTION_VERTICAL
 * });
 * ```
 */
export class WindowMaterial extends EffectCommonMaterial {
    static DIRECTION_VERTICAL: number;
    static DIRECTION_HORIZONTAL: number;
    /**
     * 构造函数
     * @param {{
    * direction?: number, speed?: number, effectSize?: Array<number>, size?: Array<number>,
    * color?: string | number | THREE.Color, opacity?: number, emissive?: string | number | THREE.Color
    * }} parameters 材质参数，所有被Material类及其子类接受的参数。
    * @param {number} parameters.direction 窗户动画方向，可选值有`WindowMaterial.DIRECTION_VERTICAL`和`WindowMaterial.DIRECTION_HORIZONTAL`
    * @param {number} parameters.speed 窗户动画速度，默认值为1
    * @param {Array<number>} parameters.size 整体效果尺寸大小，默认值为[1, 1]，单位为米
    * @param {Array<number>} parameters.effectSize 窗户动画效果大小，默认值为[0.5, 0.5]，单位为米
    * @param {string | number | THREE.Color} parameters.color 动画效果颜色，默认值为0xffffff
    * @param {number} parameters.opacity 整体效果透明度，默认值为1
    * @param {string | number | THREE.Color} parameters.emissive 自发光颜色，默认值为0x000000，即不发光
    */
    constructor(parameters: {
        direction?: number;
        speed?: number;
        effectSize?: Array<number>;
        size?: Array<number>;
        color?: string | number | THREE.Color;
        opacity?: number;
        emissive?: string | number | THREE.Color;
    });
    name: string;
    transparent: boolean;
    fragmentShader: any;
    vertexShader: any;
}
import { EffectCommonMaterial } from './EffectCommonMaterial';
