/**
 * 简单线条渲染对象，相比{@link FatLine}，SimpleLine 提供了更基础的线条渲染功能，可用于绘制1px宽度的线条
 *
 * @category Objects
 * @example
 * ```javascript
 * // 创建一个基础红色线条
 * const line = engine.add(new mapvthree.SimpleLine({
 *     color: 'rgba(250, 90, 50, 1)'
 * }));
 *
 * // 设置数据源
 * let data = mapvthree.GeoJSONDataSource.fromGeoJSON(lineData);
 * line.dataSource = data;
 * ```
 */
export class SimpleLine extends GeoLine {
    /**
     * 创建简单线条渲染对象
     * @param {Object} parameters 配置参数
     * @param {string} [parameters.color='#ffffff'] 线条颜色, 默认#ffffff
     * @param {number} [parameters.opacity=1.0] 透明度, 默认1.0
     * @param {number} [parameters.granularity=0.5] 线段细分距离(度)，仅在3D模式下生效, 默认0.5
     */
    constructor(parameters: {
        color?: string | undefined;
        opacity?: number | undefined;
        granularity?: number | undefined;
    });
    /** @protected */ protected geometry: any;
    /** @protected */ protected material: any;
    /**
     * 点颜色
     * @type {Color}
     * @example
    * ```javascript
    * line.color = 'rgb(0, 255, 0)';
    * ```
     */
    color: Color;
    parameters: {
        color?: string | undefined;
        opacity?: number | undefined;
        granularity?: number | undefined;
    };
}
import { GeoLine } from '../GeoLine';
