export class PointGroup extends GeoObject {
    /**
     * 是否支持返回的事件参数中携带entity实体数据
     */
    isEventEntitySupported: boolean;
    /**
     * 添加GeoObject类型的部件
     * @param {GeoObject} object 添加的对象
     * @returns {GeoObject} 添加的对象
     * @example
     * ```javascript
     * const icon = new Icon({
     *     width: 30,
     *     height: 30,
     *     mapSrc: 'marker.png'
     * });
     * pointGroup.addComponent(icon);
     * ```
     */
    addComponent(object: GeoObject): GeoObject;
    /**
     * 移除部件
     * @param {GeoObject} object 要移除的地理对象
     * @example
     * ```javascript
     * const icon = new Icon();
     * pointGroup.addComponent(icon);
     * // ... 其他操作 ...
     * pointGroup.removeComponent(icon); // 移除之前添加的图标组件
     * ```
    */
    removeComponent(object: GeoObject): void;
    getChildDataSource(child: any): DataSource;
}
import { GeoObject } from '../GeoObject';
