/**
 * 点对象，用于在地图上绘制基础的点标记。支持自定义颜色、大小等特性。
 *
 * @category Objects
 * @example
 * ```javascript
 * // 创建一个基础点对象
 * const point = engine.add(new mapvthree.SimplePoint({
 *     color: 'rgba(250, 90, 50, 1)',
 *     size: 50,
 *     opacity: 0.8,
 *     vertexColors: true,
 *     vertexSizes: true,
 * }));
 *
 * // 设置数据源
 * const data = mapvthree.GeoJSONDataSource.fromGeoJSON([{
 *     type: 'Feature',
 *     geometry: {
 *         type: 'Point',
 *         coordinates: [116.404, 39.915]
 *     },
 *     properties: {
 *         color: '#ff0000',
 *         size: 30
 *     }
 * }]);
 *
 * // 定义color和size属性映射
 * data.defineAttribute('color', 'color');
 * data.defineAttribute('size', 'size');
 * point.dataSource = data;
 * ```
 */
export class SimplePoint extends GeoPoints {
    /**
     * 构造函数
     * @param {Object} parameters 配置参数
     * @param {string|THREE.Color} [parameters.color] 点颜色，默认0xffff00
     * @param {boolean} [parameters.vertexColors=false] 是否启用数据驱动的颜色配置，默认false
     * @param {number} [parameters.size] 点大小，默认30
     * @param {boolean} [parameters.transparent=false] 是否启用透明，默认false
     * @param {boolean} [parameters.vertexSizes=false] 是否启用数据驱动的大小配置，默认false
     * @param {number} [parameters.opacity=1] 透明度，默认1
     */
    constructor(parameters: {
        color?: string | THREE.Color;
        vertexColors?: boolean | undefined;
        size?: number | undefined;
        transparent?: boolean | undefined;
        vertexSizes?: boolean | undefined;
        opacity?: number | undefined;
    });
    /** @protected */ protected geometry: any;
    /** @protected */ protected material: any;
    /**
     * 点颜色
     * @type {string}
     * @example
     * ```javascript
     * point.color = 'rgba(250, 90, 50, 1)';
     * ```
    */
    color: string;
    /**
     * 是否启用数据驱动的颜色配置
     * @type {boolean}
     * @default false
     * @example
     * ```javascript
     * point.vertexColors = true;
     * ```
    */
    vertexColors: boolean;
    /**
     * 是否启用数据驱动的大小配置
     * @type {boolean}
     * @default false
     * @example
     * ```javascript
     * point.vertexSizes = true;
     * ```
    */
    vertexSizes: boolean;
    /**
     * 不透明度, 若想实现半透明效果, 需要`transparent`设置为`true`
     * @type {number}
     * @default 1
     * @example
     * ```javascript
     * point.opacity = 0.8;
     * ```
    */
    opacity: number;
    parameters: {
        color?: string | THREE.Color;
        vertexColors?: boolean | undefined;
        size?: number | undefined;
        transparent?: boolean | undefined;
        vertexSizes?: boolean | undefined;
        opacity?: number | undefined;
    };
    collisionTest(data: any): {
        width: any;
        height: any;
    };
}
import { GeoPoints } from '../GeoPoints';
