/**
 * ![](media://pillar.png)
 */
export class Pillar extends GeoInstancedPointMesh {
    constructor(parameters: any);
    /** 整体透明度 */ opacity: any;
    /** 柱体高度 */ height: any;
    /** 柱体边数 */ radiusSegments: any;
    /** 柱体半径 */ radius: any;
    /** 颜色渐变 */ gradient: any;
    /** 是否通过数据携带高度配置 */ vertexHeights: any;
    /** 圆柱顶底是否打开 */ openEnded: any;
    parameters: any;
    /**
     * @internal
     * 计算高度拉升模型矩阵
     */
    getInstanceLocalMatrix: (coordinates: any, dataItem: any, index: any) => any;
    /**
     * @internal
     * 计算最大高度
     */
    addCustomAttributes: () => number | undefined;
}
import { GeoInstancedPointMesh } from '../point/GeoInstancedPointMesh';
