export class Fireflies {
    /**
     * @param {{color?: string, size?: number, countFactor?: number}} options 参数对象
     * @param {string} options.color 粒子颜色
     * @param {number} options.size 萤火虫大小
     * @param {number} options.countFactor 萤火虫数量因子，范围0~1
     */
    constructor(options?: {
        color?: string;
        size?: number;
        countFactor?: number;
    });
    /**
     * 粒子数量的比例
     * @private
     */
    private _countFactor;
    /** @private */ private _clock;
    /** @private */ private _color;
    /** @private */ private _engine;
    /** @private */ private _emitter;
    /** @private */ private _system;
    /** @private */ private _options;
    /**
     * @internal
     */
    afterAddToEngine(engine: any): void;
    /**
     * @internal
     */
    beforeRemoveFromEngine(): void;
    /**
     * @internal
     */
    overrideMaterialWithEmissive(material: any): void;
    /**
     * @internal
     */
    handleBeforeRender: (engine: any) => void;
    /**
     * @internal
     */
    update: () => void;
    /**
     * @internal
     */
    createSnow: () => any;
    /**
     * @internal
     */
    createEmitter: () => any;
    set countFactor(value: number);
    get countFactor(): number;
    dispose(): void;
}
