export class ColorAdjustPass {
    name: string;
    needsSwap: boolean;
    uniforms: any;
    material: any;
    fsQuad: any;
    render(renderer: any, writeBuffer: any, readBuffer: any): void;
    dispose(): void;
    /**
     * 饱和度
     */
    set saturation(value: any);
    get saturation(): any;
    /**
     * 对比度
     */
    set contrast(value: any);
    get contrast(): any;
    /**
     * 明度/亮度
     */
    set brightness(value: any);
    get brightness(): any;
}
