/**
 * 车道级网格线，可支持监听事件`click`
 */
export class HDMapGrid {
    constructor(tiles: any);
    _enabled: boolean;
    _apiHost: string;
    _tiles: any;
    _engine: any;
    _selectMesh: any;
    _selectDataSource: GeoJSONDataSource;
    /**
     * @private
     */
    private _setup;
    /**
     * @private
     */
    private _destroy;
    /**
     * @private
     */
    private _handleClick;
    /**
     * 是否启用车道级网格线
     */
    set enabled(value: boolean);
    get enabled(): boolean;
    /**
     * 通过经纬度获取车道级网格线信息的接口地址
     */
    set apiHost(value: string);
    get apiHost(): string;
    /**
     * 点击高亮的网格体对象
     */
    get selectMesh(): any;
}
import { GeoJSONDataSource } from '../../dataSource/GeoJSONDataSource';
