/**
 * 路况状态 0~5，默认表达如下
 * 0：无状态（通常用绿色表示）
 * 1：绿色
 * 2：黄色
 * 3：红色
 * 4：深红色
 */
export class TrafficStatusMaterial {
    constructor(parameters: any);
    fragmentShader: any;
    vertexShader: any;
    name: string;
    isTrafficStatusMaterial: boolean;
    set ready(ready: any);
    get ready(): any;
    set colorMap(colorMap: any);
    get colorMap(): any;
}
