/**
 * 物体选择器，将场景中的某个物体或单体标记为选中状态<br/>
 * 该类会随{@link Engine}的初始化自动实例化，不需要开发者手动执行初始化，通过`engine.selection`访问
 *
 * @category Internal
 * @example
 * ```javascript
 * engine.selection.select(object);
 * engine.selection.deselect(object);
 * ```
 */
export class EngineSelection {
    /**
     * @param {Engine} engine
     * @param {Object} options
     */
    constructor(engine: Engine, options?: Object);
    /**
     * @private
     */
    private _engine;
    /**
     * @private
     */
    private _boundingBoxHelper;
    /**
     * @private
     */
    private _selectedObject;
    /**
     * @private
     */
    private _transformControl;
    /**
     * 判断物体是否被选中
     * @param {Object3D} object
     * @returns {boolean}
     */
    isSelected(object: Object3D): boolean;
    /**
     * 选中物体
     * @param {Object3D} object
     */
    select(object: Object3D): void;
    /**
     * 取消选中物体
     * @param {Object3D} object
     */
    deselect(object: Object3D): void;
    get transformControl(): any;
    /**
     * 附加变换控制器
     * @param {Object3D} object
     */
    attachTransform(object: Object3D): void;
    /**
     * 分离变换控制器
     */
    detachTransform(): void;
    dispose(): void;
}
