export namespace TerrainExaggeration {
    /**
     * Scales a height relative to an offset.
     *
     * @param {Number} height The height.
     * @param {Number} scale A scalar used to exaggerate the terrain. If the value is 1.0 there will be no effect.
     * @param {Number} relativeHeight The height relative to which terrain is exaggerated.
     * If the value is 0.0 terrain will be exaggerated relative to the ellipsoid surface.
     */
    function getHeight(height: number, scale: number, relativeHeight: number): number;
}
