/**
 * 获取键盘事件的修饰键
 *
 * @param {KeyboardEvent} event - 键盘事件对象
 * @returns {KeyboardEventModifier|undefined} - 修饰键的枚举值或者 undefined
 */
export function getModifier(event: KeyboardEvent): Readonly<{
    SHIFT: number;
    CTRL: number;
    ALT: number;
}> | undefined;
/**
 * 获取输入事件的键
 *
 * @param {string} type - 输入事件的类型
 * @param {KeyboardEventModifier|undefined} modifier - 修饰键的枚举值或者 undefined
 * @returns {string} - 输入事件的键，如果有修饰键，则键的格式为 "type+modifier"，否则为 "type"
 */
export function getInputEventKey(type: string, modifier: Readonly<{
    SHIFT: number;
    CTRL: number;
    ALT: number;
}> | undefined): string;
