/**
 * 动画管理类 <br>
 * 该类会随{@link EngineRendering}的初始化自动实例化，不需要开发者手动执行初始化调用，通过`engine.rendering.animation`访问<br/>
 * 该类默认创建了一个全局动画混合器，开发者可以通过`engine.rendering.animation.mixer`访问，
 * 也可以通过`engine.rendering.animation.addMixer`添加自定义动画混合器<br/>
 *
 * @category Internal
 * @example
 * ```javascript
 * engine.rendering.animation.addMixer(mixer);
 * ```
 * @example
 * ```javascript
 * const action1 = engine.rendering.animation.mixer.clipAction(clip);
 * action1.play();
 * ```
 */
export class RenderingAnimation {
    constructor(rendering: any);
    /**
     * @private
     */
    private _rendering;
    /**
     * @private 默认动画混合器
     */
    private _mixer;
    /** @private */
    private _customMixers;
    update(renderState: any): void;
    /**
     * 添加自定义动画混合器
     * @param {AnimationMixer} mixer - 自定义动画混合器
     */
    addMixer(mixer: AnimationMixer): void;
    /**
     * 移除自定义动画混合器
     * @param {AnimationMixer} mixer - 自定义动画混合器
     */
    removeMixer(mixer: AnimationMixer): void;
    /**
     * 获取默认动画混合器
     * @returns {AnimationMixer} 默认动画混合器
     */
    get mixer(): AnimationMixer;
}
