export class RenderingShadow {
    static DEFAULT: number;
    static CSM: number;
    constructor(rendering: any);
    /** @private */
    private _rendering;
    /**
     * @type {number}
     * 适用于简单投影模型，平行光只用一个shadowMap
     * 平行光投影的基准高度，越接近此值，阴影越清晰，阴影范围越小
     */
    _baseHeight: number;
    /** @private */
    private _mode;
    set enabled(enabled: any);
    get enabled(): any;
    set baseHeight(height: number);
    get baseHeight(): number;
}
