/**
 * 集中管理的全屏渲染目标，用作后处理或全屏绘图使用，没有深度和模板
 * 主要用于后处理的中间渲染目标
 * 例如FXAA, SSAO等
 * 使用是get,使用完立即release，以使得buffer尽可能复用
 * 不包含主渲染流程中的buffer
 */
export class SharedFullScreenRenderTargets {
    constructor(rendering: any);
    /** @private
     * @type {RenderTarget}
     */
    private _renderTargets;
    /** @private
     * 锁
    */
    private _locks;
    /** @private */
    private _maxRenderTargets;
    _rendering: any;
    beginFrame(): void;
    _disposeRenderTargets(): void;
    _resetLocks(): void;
    getAvailableRenderTarget(): RenderTarget;
    releaseRenderTarget(renderTarget: any): void;
    releaseRenderTargetByIndex(index: any): void;
    lockRenderTarget(renderTarget: any): void;
    setSize(width: any, height: any): void;
    endFrame(): void;
}
