/**
 * 控件基类，所有控件都继承自此类
 * @category Internal
 */
export class BaseWidgets {
    /**
     * @param {HTMLElement} container 容器元素
     * @param {object} options 控件配置
     * @param {boolean} options.enabled 是否启用控件
     * @param {Engine} engine 引擎实例
     */
    constructor(container: HTMLElement, options: {
        enabled: boolean;
    }, engine?: Engine);
    /** @private */ private _container;
    /** @private */ private _enabled;
    /** @private */ private _options;
    /** @private */ private _element;
    /** @private */ private _engine;
    /**
     * 初始化控件
     * @protected
     */
    protected init(): void;
    /**
     * 初始化后处理
     * @protected
     */
    protected afterInit(): void;
    /**
     * 创建DOM元素
     * @protected
     * @returns {HTMLElement}
     */
    protected createDom(): HTMLElement;
    /**
     * 释放资源
     */
    dispose(): void;
    /**
     * 清理事件监听
     * @protected
     */
    protected onDispose(): void;
    /**
     * 获取容器元素
     * @type {HTMLElement}
     */
    get container(): HTMLElement;
    /**
     * 获取控件元素
     * @type {HTMLElement}
     */
    get element(): HTMLElement;
    /**
     * 设置控件是否启用
     * @type {boolean}
     */
    set enabled(value: boolean);
    /**
     * 获取控件是否启用
     * @type {boolean}
     */
    get enabled(): boolean;
}
