/**
 * 坡度分析
 * ```js
 * const slopeAnalysis = new mapvthree.SlopeAnalysis({
 * 	 // 坡度颜色分带，默认如下
 *   colorStop: {
 *      0.5: 'rgb(85,182,43)',
 *      2: 'rgb(135,211,43)',
 *      5: 'rgb(204,244,440',
 *      15: 'rgb(245,233,44)',
 *      35: 'rgb(255,138,43)',
 *      55: 'rgb(255,84,43)',
 *      90: 'rgb(255,32,43)',
 *   }
 * });
 * engine.add(volumeLandAnalysis);
 * // 启用坡度分析
 * slopeAnalysis.enabled = true;
 * // 指定区域
 * const position = [
 *      [113.07287725760811, 25.221444101005975],
 *      [113.09542969643496, 25.229863262042286],
 *      [113.1034260130611, 25.212378698502196],
 *      [113.0721098332844, 25.203326178361227],
 * ]
 * slopeAnalysis.coverageArea = position;
 * // 开始坡度分析
 * this._slopeAnalsis.build();
 * ```
 */
export class SlopeAnalysis {
    constructor(options: any);
    /** @private */ private _coverageArea; /** 坡度分析范围，为位置数组，若不指定则计算全局坡度 */
    /** @private */ private _slopeMap; /** 坡度颜色纹理 */
    /** @private */ private _maxSlope;
    /** @private */ private _minSlope;
    /** @private */ private _enabled;
    /** @private */ private _groundObject;
    /** @private */
    private _defaultcolorStop;
    _options: any;
    _colorStop: any;
    afterAddToEngine(engine: any): void;
    _engine: any;
    set minSlope(value: number);
    get minSlope(): number;
    set maxSlope(value: number);
    get maxSlope(): number;
    /**
     * 设置颜色分段信息
     * @param {object} colorStop 颜色分段信息
     * key值为坡度大小, 0-90
     * value为颜色值
     */
    setColorStop(colorStop: object): void;
    /**
     * 开始坡度分析
     */
    build(): void;
    /**
     * 结束坡度分析
     */
    close(): void;
    set enabled(value: boolean);
    get enabled(): boolean;
    beforeRemoveFromEngine(): void;
    set coverageArea(value: any[]);
    get coverageArea(): any[];
}
