/**
 * 注意：
 * 1. 高度数据根据射线检测获取，若要保证结果的准确性，请确保地形瓦片在当前视图内
 * 2. 提高通过slopeAnalysis.interpolateNum修改采样数，采样点可以提升精度，但同时会增加计算量，建议根据实际需求调整设置
 * ```js
 * const volumeLandAnalysis = engine.add(new mapvthree.VolumeLandAnalysis(engine), {
 *      terrain: terrain,
 * });
 * engine.add(volumeLandAnalysis);
 * // 分析区域，坐标为墨卡托坐标
 * const position = [
 *      [113.04617524014166,25.24069362073602],
 *      [113.04924283736078,25.245297836937276],
 *      [113.05294688497264,25.241156660528866],
 *      [113.05173456140149,25.234709924929895],
 *      [113.0436390581951,25.234834803843658]
 * ]
 * volumeLandAnalysis.coveragePosition = position;
 * // 基准高度
 * volumeLandAnalysis.surfaceHeight = surfaceHeight;
 * // 打开采样（默认关闭，为填挖模式）
 * volumeLandAnalysis.showSamplePoint = true;
 * volumeLandAnalysis.build();
 * // 获取方量分析结果
 * const {
 *      cutArea,
 *      cutVolume,
 *      fillArea,
 *      fillVolume
 * } = volumeLandAnalysis;
 * ```
 */
export class VolumeLandAnalysis {
    constructor(engine: any, options?: {});
    /** @private */ private _coveragePosition;
    /** @private */ private _surfaceHeight;
    /** @private */ private _interpolateNum;
    /** @private */ private _polygonPosition;
    /** @private */ private _cutArea;
    /** @private */ private _cutVolume;
    /** @private */ private _fillArea;
    /** @private */ private _fillVolume;
    /** @private */ private _terrain;
    /** @private */ private _terrainClipPlan;
    /** @private */ private _terrainClipPlanOpts;
    /** @private */ private _showSamplePoint;
    /** @private */ private _enabled;
    /** @private */ private _samplePoint;
    afterAddToEngine(engine: any): void;
    _engine: any;
    build(): void;
    /**
     * @private
     * 生成网格点
     */
    private _calcInterpolatePoints;
    /**
     * @private
     * 采样网格点高度
     */
    private _sampleGridHeights;
    /**
     * @private
     * 过滤不在多边形polygon中的三角形
     */
    private _filterGridList;
    /**
     * @private
     * 根据三角网计算填挖方面积/体积
     */
    private calcVolumeLand;
    /**
     * @private
     * 计算三角格网的顶点索引
     */
    private getGridIndex;
    /**
     * @private
     * 判断一个点是否在一个多边形内
     */
    private isInPolygon;
    clear(): void;
    set surfaceHeight(value: number);
    /**
     * 开填挖基准高度
     */
    get surfaceHeight(): number;
    set coveragePosition(value: any[]);
    get coveragePosition(): any[];
    set showSamplePoint(value: boolean);
    get showSamplePoint(): boolean;
    set interpolateNum(value: number);
    /**
     * 每行的插值数
     */
    get interpolateNum(): number;
    /**
     * 挖方面积
     */
    get cutArea(): number;
    /**
     * 挖方体积
     */
    get cutVolume(): number;
    /**
     * 填方面积
     */
    get fillArea(): number;
    /**
     * 填方体积
     */
    get fillVolume(): number;
    beforeRemoveFromEngine(engine: any): void;
}
