export class RasterSurfaceMaterial {
    constructor(parameters?: {});
    name: string;
    fragmentShader: any;
    vertexShader: any;
    defines: {
        NUM_TEXTURES: number;
    };
    uniforms: any;
    updateTextures: (textureConfigs: any, boundRect: any) => void;
    needsUpdate: boolean | undefined;
    forceUpdateTextureCount: (textureCount: any) => void;
    get textureCount(): number;
    dispose(): void;
}
