/**
 * 定义瓦片加载
 */
export class TileLoader {
    constructor(options?: {});
    isTileLoader: boolean;
    /** @private */
    private _ready;
    /** @private */
    private _maxLevel;
    _options: {};
    init(): void;
    afterInit(): void;
    loadTile(tile: any): void;
    disposeTile(tile: any): void;
    dispose(): void;
    get ready(): boolean;
    get maxLevel(): number;
}
