/**
 * 影像瓦片提供者，负责影像瓦片数据的请求和缓存。<br/>
 * 该类是所有影像瓦片提供者的**抽象基类，不可直接使用，需要使用子类。**
 *
 * @category Internal
 */
export class ImageryTileProvider extends TileProvider {
    constructor(options?: {});
    isImageryTileProvider: boolean;
    _opacity: any;
    _colorTint: any;
    _randomColorTint: any;
    getTileURL(z: any, x: any, y: any, tile: any): void;
    /**
     * 判断是否需要进行投影变换，取中心点的像素坐标，计算变换后的像素坐标，判断是否有明显的偏移
     * @param {*} sourceProjection
     * @param {*} targetProjection
     * @param {*} tile
     * @returns
     */
    shouldReproject(sourceProjection: any, targetProjection: any, tile: any): boolean;
    /**
     * 请求瓦片数据
     *
     * @param {*} tile - 瓦片对象
     */
    doRequestTileData(tile: any): Promise<any>;
    errorFeedback(): any;
    set opacity(opacity: any);
    get opacity(): any;
    set colorTint(colorTint: any);
    get colorTint(): any;
    set randomColorTint(randomColorTint: any);
    get randomColorTint(): any;
}
import { TileProvider } from '../TileProvider';
