/**
 * OpenStreetMap影像瓦片提供者，用于加载和渲染OSM地图的影像瓦片数据。
 * 支持Web墨卡托和地理坐标两种投影方式。
 *
 * 主要功能：
 * - 支持OpenStreetMap影像瓦片加载
 * - 支持Web墨卡托和地理坐标投影
 * - 支持瓦片URL的自动生成
 * - 支持多服务器负载均衡
 *
 * @category MapView
 * @example
 * ```javascript
 * // 创建OSM影像瓦片提供者
 * const provider = new OSMImageryTileProvider({
 *     // 配置选项
 * });
 * ```
 */
export class OSMImageryTileProvider extends ImageryTileProvider {
    /**
     * @param {object} options 配置选项
     */
    constructor(options: object);
    /**
     * 瓦片提供者名称
     * @readonly
     */
    readonly name: "OSMImageryTileProvider";
    /**
     * 支持的投影方式
     * @private
     */
    private _supportedTargetProjectionNames;
    /**
     * 默认起始缩放级别
     * @private
     */
    private _defaultStartLevel;
    /**
     * 默认最大缩放级别
     * @private
     */
    private _defaultMaxLevel;
    /**
     * 获取瓦片URL
     * @internal
     * @param {number} z 缩放级别
     * @param {number} x 瓦片X坐标
     * @param {number} y 瓦片Y坐标
     * @param {object} tile 瓦片对象
     * @returns {string} 瓦片URL
     */
    getTileURL(z: number, x: number, y: number, tile: object): string;
}
import { ImageryTileProvider } from './ImageryTileProvider';
