/**
 * 平面地形瓦片提供者，用于生成和渲染平面地形瓦片。支持多种投影方式，包括Web墨卡托、ECEF和地理坐标系统。
 * 主要功能：
 * - 支持多种投影方式的地形瓦片生成
 * - 提供基础的地形几何数据
 * - 支持瓦片数据的请求和处理
 *
 * @category MapView
 * @example
 * ```javascript
 * const provider = new PlaneTerrainTileProvider({
 *     // 配置选项
 * });
 * ```
 */
export class PlaneTerrainTileProvider extends TerrainTileProvider {
    /**
     * 瓦片提供者名称
     * @readonly
     */
    readonly name: "PlaneTerrainTileProvider";
    /**
     * 支持的目标投影方式
     * @private
     * @type {string[]}
     */
    private _supportedTargetProjectionNames;
    /**
     * 零级最大几何误差
     * @private
     * @type {number}
     */
    private _levelZeroMaximumGeometricError;
    rasterProjection: any;
    /**
     * 请求瓦片数据
     * @internal
     * @param {Tile} tile 需要请求的瓦片对象
     * @returns {BufferGeometry} 返回包含地形数据的几何体
     */
    doRequestTileData(tile: Tile): BufferGeometry;
    /**
     * 将Vector3对象转换为数组
     * @private
     * @param {Vector3} vector 三维向量
     * @returns {number[]} 转换后的数组
     */
    private _vectorToArray;
    /**
     * 将Box3对象转换为数组
     * @private
     * @param {Box3} box 三维包围盒
     * @returns {number[]} 转换后的数组
     */
    private _boxToArray;
}
import { TerrainTileProvider } from './TerrainTileProvider';
