/**
 * 矢量瓦片提供者，负责矢量瓦片数据的请求和缓存。<br/>
 * 该类是所有矢量瓦片提供者的**抽象基类，不可直接使用，需要使用子类。**
 *
 * @category Internal
 */
export class VectorTileProvider extends TileProvider {
    isVectorTileProvider: boolean;
    errorFeedback(): any[];
    /**
     * @param {*} tile
     * @returns
     * Array[object, groundObject]：包含对象的完整transform
     * object: 在3D场景中渲染的对象
     * groundObject: 在地面上渲染的对象
     * @desc 请求瓦片数据
     */
    doRequestTileData(tile: any): Promise<void>;
    getGroundTileData(tile: any): any[];
    _groundTileMaterial: any;
}
import { TileProvider } from '../TileProvider';
