/**
 * 单体模型，简易加载方式，兼容多种投影方式。用于加载单个3D模型并设置其位置、旋转和缩放。
 *
 * @category Model
 * @example
 * ```javascript
 * let model = engine.add(new mapvthree.SimpleModel({
 *  name: '模型名称',
 *  url: '模型路径',
 *  point: [lng, lat, z],
 *  scale: [1, 1, 1],
 *  rotation: [0, 0, 0],
 * }))
 * ```
 *
 * @example
 * ```javascript
 * // 监听模型加载完成事件
 * model.addEventListener('loaded', e => {
 *     console.log('模型加载完成:', e.value);
 * });
 * ```
 */
export class SimpleModel {
    /**
     * 构造函数
     * @param {Object} parameters 配置参数
     * @param {string} [parameters.name=''] 模型名称
     * @param {string} [parameters.object] 模型URL文件路径或Object3D实例，URL模型支持glb/gltf格式
     * @param {Array<number>|Vector3} [parameters.point=[0,0,0]] 模型位置，格式为[经度,纬度,高度]
     * @param {Array<number>|Vector3|number} [parameters.scale=[1,1,1]] 模型缩放
     * @param {Array<number>|Vector3} [parameters.rotation=[0,0,0]] 模型旋转,x,y,z三个分量分别代表roll,pitch,heading，单位为弧度
     * @param {boolean} [parameters.autoYUpToZUp=true] 是否自动将Y轴向上转换为Z轴向上,默认true,仅对通过URL加载的模型有效
     */
    constructor(parameters: {
        name?: string | undefined;
        object?: string | undefined;
        point?: Array<number> | Vector3;
        scale?: Array<number> | Vector3 | number;
        rotation?: Array<number> | Vector3;
        autoYUpToZUp?: boolean | undefined;
    });
    /** @internal */
    _autoYUpToZUp: boolean;
    _parameters: {
        name?: string | undefined;
        object?: string | undefined;
        point?: Array<number> | Vector3;
        scale?: Array<number> | Vector3 | number;
        rotation?: Array<number> | Vector3;
        autoYUpToZUp?: boolean | undefined;
    };
    _name: string;
    _object: any;
    afterAddToEngine(engine: any): void;
    _engine: any;
    beforeRemoveFromEngine(): void;
    _initModel(): Promise<void>;
    dispose(): void;
    /**
     * 设置模型的变换参数
     * @param {Object} transform 变换参数对象
     * @param {Array<number>|Vector3} [transform.point] 位置坐标[经度,纬度,高度]
     * @param {Array<number>|Vector3} [transform.rotation] 旋转角度，x,y,z三个分量分别代表roll,pitch,heading (单位为弧度)
     * @param {Array<number>|Vector3|number} [transform.scale] 缩放比例
     *
     * @example
     * ```javascript
     * // 设置位置、旋转和缩放
     * model.setTransform({
     *     point: [116.404, 39.915, 100],
     *     rotation: [Math.PI/2, 0, 0],
     *     scale: [2, 2, 2]
     * });
     *
     * // 只设置位置
     * model.setTransform({
     *     point: [116.404, 39.915, 100]
     * });
     *
     * // 使用Vector3设置
     * model.setTransform({
     *     point: new Vector3(116.404, 39.915, 100),
     *     scale: new Vector3(2, 2, 2)
     * });
     * ```
    */
    setTransform(transform?: {
        point?: Array<number> | Vector3;
        rotation?: Array<number> | Vector3;
        scale?: Array<number> | Vector3 | number;
    }): void;
    /**
     * 设置模型位置
     * @param {Array<number>|Vector3} value 位置坐标[经度,纬度,高度]
     * @example
    * ```javascript
    * model.point = [116.404, 39.915, 100];
    * ```
    */
    set point(value: Array<number> | Vector3);
    /**
     * 是否自动将Y轴向上转换为Z轴向上，仅对通过URL加载的模型有效
     * @type {boolean}
     */
    get autoYUpToZUp(): boolean;
    _updateData(): void;
}
