/**
 * 默认天空效果，提供简单的渐变天空效果，是性能开销与效果比较平衡的天空。
 *
 * DefaultSky类是{@link EmptySky}的扩展，添加了一个梯度天空后处理通道，可以创建简单的渐变天空效果，适用于不需要高级天空效果的场景。
 *
 * 如果需要更复杂的天空效果，可以使用{@link DynamicSky}
 *
 * @category Natural
 * @example
 * ```javascript
 * const sky = engine.add(new mapvthree.DefaultSky());
 * sky.color = new THREE.Color(0x87ceeb); // 设置天空基础颜色
 * sky.highColor = new THREE.Color(0x67ceeb); // 设置天空高处颜色
 * ```
 */
export class DefaultSky extends EmptySky {
    /**
     * 标识为DefaultSky
     * @type {boolean}
     * @readonly
     */
    readonly isDefaultSky: boolean;
    /**
     * 天空名称
     * @type {string}
     * @readonly
     */
    readonly name: string;
    /** @private */ private _postPass;
    /** @private */ private _engine;
    _color: any;
    _highColor: any;
    /**
     * 初始化环境和后处理效果
     * @private
     */
    private initEnv;
    /**
     * 设置天空高处颜色
     * @type {Color}
     */
    set highColor(color: Color);
    /**
     * 获取天空高处颜色
     * @type {Color}
     */
    get highColor(): Color;
    /**
     * 设置天空基础颜色
     * @type {Color}
     */
    set color(color: Color);
    /**
     * 获取天空基础颜色
     * @type {Color}
     */
    get color(): Color;
    /**
     * 设置是否启用天空后处理效果
     * @type {boolean}
     * @internal
     */
    set enablePostPass(value: boolean);
    /**
     * 是否启用天空后处理效果
     * @type {boolean}
     * @internal
     */
    get enablePostPass(): boolean;
}
import { EmptySky } from './EmptySky';
