/**
 * 图片标注覆盖物，用于在地图上添加图片标记。继承自{@link DOMOverlay}。
 *
 * @category Objects
 * @example
 * ```javascript
 * // 创建一个图片标注
 * const marker = engine.add(new mapvthree.Marker({
 *     point: [116.404, 39.915, 0],
 *     icon: 'path/to/icon.png',
 *     width: 32,
 *     height: 32,
 *     offset: [0, -16]  // 将锚点设置到图片底部中心
 * }));
 * ```
 */
export class Marker extends DOMOverlay {
    /**
     * 创建图片标注
     * @param {Object} parameters 配置参数
     * @param {string} [parameters.icon='https://webmap0.bdimg.com/image/api/marker_red.png'] 图标URL
     * @param {number} [parameters.width=25] 图标宽度, 默认值：25
     * @param {number} [parameters.height=25] 图标高度, 默认值：25
     * @param {Array<number>} [parameters.point] 位置坐标 [经度,纬度,高度], 默认值：[0,0,0]
     * @param {Array<number>} [parameters.offset=[0,0]] 像素偏移量, 默认值：[0,0]
     * @param {boolean} [parameters.enableDragging=false] 是否可拖拽, 默认值：false
     */
    constructor(parameters?: {
        icon?: string | undefined;
        width?: number | undefined;
        height?: number | undefined;
        point?: number[] | undefined;
        offset?: number[] | undefined;
        enableDragging?: boolean | undefined;
    });
    /** @private */ private _icon;
    /** @private */ private _width;
    /** @private */ private _height;
    isMarker: boolean;
    initDom(): HTMLImageElement;
    /**
     * 设置图片路径
     * @param {string} value 图片路径
     * @example
     * ```javascript
     * marker.icon = 'path/to/icon.png';
     * ```
     */
    set icon(value: string);
    /**
     * 获取图片路径
     * @returns {string} 图片路径
     */
    get icon(): string;
    /**
     * 设置图片宽度
     * @param {number} value 图片宽度
     */
    set width(value: number);
    /**
     * 获取图片宽度
     * @returns {number} 图片宽度
     */
    get width(): number;
    /**
     * 设置图片高度
     * @param {number} value 图片高度
     */
    set height(value: number);
    /**
     * 获取图片高度
     * @returns {number} 图片高度
     */
    get height(): number;
}
import { DOMOverlay } from './DOMOverlay';
