/**
 * 信息弹窗覆盖物，用于在地图上显示标题和内容信息。继承自{@link DOMOverlay}。
 *
 * @category Objects
 * @example
 * ```javascript
 * // 创建一个基础信息弹窗
 * const popup = engine.add(new mapvthree.Popup({
 *     point: [116.404, 39.915, 0],
 *     title: '位置信息',
 *     content: '北京市天安门广场',
 *     offset: [0, -20],  // 向上偏移20像素
 * }));
 *
 * // 动态更新内容
 * popup.content = '新的内容';
 * popup.title = '新的标题';
 * ```
 */
export class Popup extends DOMOverlay {
    constructor(parameters: any);
    /** @private */ private _titleDiv;
    /** @private */ private _contentDiv;
    /** @private */ private _title;
    /** @private */ private _content;
    /** @private */ private _closePopup;
    isPopup: boolean;
    initDom(): HTMLDivElement;
    /**
     * 设置标题
     * @param {string} value 标题
     */
    set title(value: string);
    /**
     * 获取标题
     * @returns {string} 标题
     */
    get title(): string;
    /**
     * 设置内容
     * @param {string} value 内容
     */
    set content(value: string);
    /**
     * 获取内容
     * @returns {string} 内容
     */
    get content(): string;
    click: () => void;
}
import { DOMOverlay } from './DOMOverlay';
