/**
 * 将颜色转化为长度为4的数组，包含rgba信息
 * @internal
 * @param {number | string} params 传入颜色，支持16进制、rgb、rgba、hsl、hsla等格式
 * @returns {number[]} 返回一个包含rgba信息的数组
 */
export function colorToArr4(params: number | string): number[];
export function colorToRgbaArr(params: any): any[];
/**
 * 将颜色转化为THREE.Color类型，不含透明度信息
 * @internal
 * @param {number | string} params 传入颜色，支持16进制、rgb、rgba、hsl、hsla等格式
 * @returns {number[]} 返回一个THREE.Color类型的颜色
 */
export function normalizeColor(params: number | string): number[];
/**
 * 转换颜色值，在转换时保证能经过THREE.Color，从而在正确的色彩空间内计算颜色
 * 1. 当色值为8为hex或者rgba时，返回Vector4，包含透明度信息
 * 2. 其他情况返回Color，只包含rgb信息
 * @internal
 * @param {number | string} params 传入颜色，支持16进制、rgb、rgba、hsl、hsla等格式
 * @returns {Color | Vector4} 返回Color或Vector4类型的颜色信息
 */
export function convertSRGBColor(params: number | string): Color | Vector4;
export function rgb565torgb(rgb565: any): number[];
